INSERT INTO about (title, content, section, order_index, created_at, updated_at) VALUES
('О нашей студии', 'Студия «МК Восковые ручки» — это место, где рождается магия творчества. Мы создаем уникальные 3D-копии рук детей в восковом исполнении.', 'main', 1, NOW(), NOW()),
('Наши услуги', 'Мы предлагаем различные варианты восковых ручек: классические, световые, с подсветкой и другие. Каждая ручка создается индивидуально для вашего ребенка.', 'services', 2, NOW(), NOW()),
('Как это работает', 'Процесс создания восковой ручки занимает всего 7 минут. Мы используем специальный воск, который безопасен для детей и создает точную копию руки.', 'process', 3, NOW(), NOW()),
('Почему мы', 'Наша студия имеет многолетний опыт в создании восковых ручек. Мы работаем с детьми любого возраста и создаем качественные сувениры, которые будут радовать вас долгие годы.', 'why_us', 4, NOW(), NOW());
